                                                                                                                                                                                                                                                                                                                                                                               var trainCalandarYear=$('#TrainYear').val();

                                                                                                                                                                                                                                                                                                                                                                               var userYear;

                                                                                                                                                                                                                                                                                                                                                                               var mySplitResult = trainYear.split("-");

                                                                                                                                                                                                                                                                                                                                                                               for (i = 0; i < mySplitResult.length; i++) {
                                                                                                                                                                                                                                                                                                                                                                                   if(mySplitResult[i].length==4){
                                                                                                                                                                                                                                                                                                                                                                                       userYear=mySplitResult[i];
                                                                                                                                                                                                                                                                                                                                                                                   }
                                                                                                                                                                                                                                                                                                                                                                               }



                                                                                                                                                                                                                                                                                                                                                                               if (trainYear  && (trainCalandarYear == userYear)) {
                                                                                                                                                                                                                                                                                                                                                                                   return true;
                                                                                                                                                                                                                                                                                                                                                                               }
                                                                                                                                                                                                                                                                                                                                                                               return false;
                                                                                                                                                                                                                                                                                                                                                                           }, ""
